Bot.onMessage = function(msg) {
	if(!msg.author.bot) {
		try {
			this.checkCommand(msg, function(checked) {
				if(!checked) Bot.onAnyMessage(msg);
			});							
		} catch(e) {
			console.error(e);
		}
	}
};
