Bot.checkCommand = function(msg, callback) {
	try {
		const defaultTag = process.env.BOT_TAG || Files.data.settings.tag;
		const separator = Files.data.settings.separator || '\\s+';

		let content = msg.content;
		let guildid   = (msg.guild && msg.guild.id) ? msg.guild.id : "000";

		content = content.split(new RegExp(separator))[0];

		Bot.Subscriber.findOne({ serverid: guildid },function(err, subscriber){
			if (err) log(err.stack ? err.stack : err);

			const tag = (subscriber && subscriber.tag) ? subscriber.tag : defaultTag;
			if(tag){	

				if(msg.guild) msg.guild.tag = tag;	

				if(content.startsWith(tag)) {				
					let command = content.substring(tag.length);
					if(command) {       
						if(!Bot._caseSensitive) {
							command = command.toLowerCase();
						}
						const cmd = Bot.$cmds[command];
						if(cmd) {				
							// metrics? get used commands here for metrics!			
							Actions.preformActions(msg, cmd);
							if(callback) callback(true);
						}
					}				
				}
			}			
			if(callback) callback(false);	
		});
	} catch (e) {
		console.error(e);
	}

};
